import React, { useState } from 'react';
import { useCatalogContext } from '../hooks/useCatalogContext';
import { useNavigate } from 'react-router-dom'; 
import Select from "react-select";
import axios from "axios";

function UploadHouseForm() {
  const [tags, setTags] = useState("");
  const [file, setFile] = useState("");
  const [allImage, setAllImage] = useState(null);
  const [pdfFile, setPdfFile] = useState(null);

  useEffect(() => {
    getPdf();
  }, []);
  const getPdf = async () => {
    const result = await axios.get("http://localhost:4000/get-files");
    console.log(result.data.data);
    setAllImage(result.data.data);
  };

  const submitImage = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("file", file);
    console.log(title, file);

    const result = await axios.post(
      "http://localhost:4000/upload-files",
      formData,
      {
        headers: { "Content-Type": "multipart/form-data" },
      }
    );
    console.log(result);
    if (result.data.status == "ok") {
      alert("Uploaded Successfully!!!");
      getPdf();
    }
  };
  const showPdf = (pdf) => {
    // window.open(`http://localhost:5000/files/${pdf}`, "_blank", "noreferrer");
    setPdfFile(`http://localhost:5000/files/${pdf}`)
  };
 
  const tagOptions = [
    { value: "residential", label: "Residential" },
    { value: "business", label: "Business" },
    { value: "commercial", label: "Commercial" },
    { value: "farm", label: "Farm" },
    { value: "infrastructure", label: "Infrastructure" },
    { value: "sustainable", label: "Sustainable" },
    { value: "tiny house", label: "Tiny House" },
    { value: "single-story", label: "Single-Story" },
    { value: "two-story", label: "Two-Story" },
    { value: "hut", label: "Hut" },
    { value: "component", label: "Component" },
    { value: "decorative", label: "Decorative" },
    { value: "retaining", label: "Retaining" },
    { value: "earthquake", label: "Earthquake" },
    { value: "hurricane", label: "Hurricane" },
    { value: "Tudor", label: "Tudor" },
    { value: "cape cod", label: "Cape Cod" },
    { value: "Mediterranean", label: "Mediterranean" },
    { value: "southwest", label: "Southwest" },
    { value: "modern", label: "Modern" },
    { value: "craftsman", label: "Craftsman" },
    { value: "contemporary", label: "Contemporary" },
    { value: "specialty", label: "Specialty" },
    { value: "industrial", label: "Industrial" },
    { value: "beach", label: "Beach" },
    { value: "bungalow", label: "Bungalow" },
    { value: "cabin", label: "Cabin" },
    { value: "colonial", label: "Colonial" },
    { value: "classical", label: "Classical" },
    { value: "farmhouse", label: "Farmhouse" },
    { value: "ranch", label: "Ranch" },
    { value: "prairie", label: "Prairie" },
    { value: "southern", label: "Southern" },
    { value: "Victorian", label: "Victorian" },
  ];


  
  return (
    <div className="form-container">
      <form className="create" onSubmit={submitImage}> 
        <h3>Upload House Model</h3>

        <label htmlFor="structureType">Structure Type</label>
        <input 
          type="text" 
          id="structureType" 
          name="structureType" 
          value={formData.structureType} 
          readOnly // Prevent editing
        />

        <label htmlFor="userId">User ID</label>
        <input 
          type="text" 
          id="userId" 
          name="userId" 
          value={formData.userId} 
          readOnly // Prevent editing
        />

        <label htmlFor="images">Images</label>
        <input 
          type="file" 
          id="images" 
          name="images" 
          onChange={handleImageChange} 
          multiple 
          accept="image/*" 
          className={emptyFields.includes('images') ? 'error' : ''}
        />

        <label htmlFor="tags">Tags</label>
        <Select
          id="tags"
          name="tags"
          value={selectedTags.map(tag => ({ value: tag, label: tag }))}
          onChange={(e) => setTitle(e.target.value)}
          options={tagOptions}
          isMulti
        /><br/>

        <label htmlFor="files">Files</label>
        <input 
          type="file" 
          id="files" 
          name="files" 
          onChange={(e) => setFile(e.target.files[0])} 
          multiple 
          className={emptyFields.includes('files') ? 'error' : ''}
        />

        <div>
          <button type="submit">Submit</button>
          <button type="button" onClick={handleCancel}>Cancel</button>
        </div>

        {error && <div className="error">{error}</div>}

        {formData.files.length > 0 && (
          <div>
            <h4>Selected Files:</h4>
            <ul>
              {formData.files.map((file, index) => (
                <li key={index}>{file.name}</li>
              ))}
            </ul>
          </div>
        )}
      </form>
        </div>
);
};

export default UploadHouseForm;
